import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { calculateBinaryAddition, calculateBinarySubtraction } from './addorsubtract';

function BinaryOperationSimulator() {
  const [binary1, setBinary1] = useState("");
  const [binary2, setBinary2] = useState("");
  const [operation, setOperation] = useState("addition");
  const [steps, setSteps] = useState([]);
  const [result, setResult] = useState("");

  const navigate = useNavigate();

  const handleOperation = () => {
    if (!binary1 || !binary2) {
      alert("Please enter both binary numbers");
      return;
    }

    let steps = [];
    let result = "";

    if (operation === "addition") {
      const response = calculateBinaryAddition(binary1, binary2);
      steps = response.steps;
      result = response.result;
    } else if (operation === "subtraction") {
      const response = calculateBinarySubtraction(binary1, binary2);
      steps = response.steps;
      result = response.result;
    }

    // Navigate to a page that shows the result
    navigate("/", { state: { result, steps } });
  };

  return (
    <div className="container">
      <div className="card">
        <h2>Add or Subtract</h2>
        <div className="card-body">
          <div className="form-group">
            <label>Addition</label>
            <button className="btn">Addition</button>
          </div>
          <div className="form-group">
            <label>Subtraction</label>
            <button className="btn">Subtraction</button>
          </div>
        </div>
      </div>

      <div className="card">
        <h2>Binary Addition</h2>
        <div className="card-body">
          <div className="form-group">
            <label>Enter First Binary Number:</label>
            <input type="text" value={binary1} onChange={(e) => setBinary1(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Enter Second Binary Number:</label>
            <input type="text" value={binary2} onChange={(e) => setBinary2(e.target.value)} />
          </div>
          <button className="btn" onClick={handleOperation}>Calculate</button>
          <div className="steps">
            <h3>Conversion Steps</h3>
            <pre>{steps.join("\n")}</pre>
            <h3>Result</h3>
            <pre>{result}</pre>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BinaryOperationSimulator;
